import React, { useState } from 'react';
import axios from 'axios';
import { TextField, Button, Container, Typography, Box } from '@mui/material';
import DataTable from './DataTable';
import { saveAs } from 'file-saver';
import { ScatterChart, Scatter, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

const MainComponent = () => {
    const [keywords, setKeywords] = useState('');
    const [mergedData, setMergedData] = useState([]);

    const handleKeywordsChange = (event) => {
        setKeywords(event.target.value);
        console.log('Keywords changed:', event.target.value);
    };

    const handleFetchData = async () => {
        const keywordList = keywords.split(',').map(keyword => keyword.trim());
        console.log('Fetching data for keywords:', keywordList);

        const apiKey = process.env.REACT_APP_JUNGLE_SCOUT_API_KEY;
        const keyName = process.env.REACT_APP_JUNGLE_SCOUT_KEY_NAME;
        const url = "https://developer.junglescout.com/api/product_database_query";

        const headers = {
            'Authorization': `${keyName}:${apiKey}`,
            'X-API-Type': 'junglescout',
            'Accept': 'application/vnd.junglescout.v1+json',
            'Content-Type': 'application/vnd.api+json',
        };

        const promises = keywordList.map((keyword) =>
            axios.post(url, {
                data: { attributes: { keyword, marketplace: 'us' } },
            }, { headers })
        );

        try {
            const responses = await Promise.all(promises);
            console.log('API responses received:', responses);

            const results = responses.map((response) => response.data);
            const flattenedResults = results.flatMap(result => result.data.map(item => ({
                price: item.attributes.price,
                sales: item.attributes.approximate_30_day_units_sold
            })));
            console.log('Flattened results:', flattenedResults);

            setMergedData(flattenedResults);
        } catch (error) {
            console.error("API request failed", error);
        }
    };

    const saveToFile = () => {
        console.log('Saving data to file...');
        const csvHeaders = Object.keys(mergedData[0]).join(',');
        const csvRows = mergedData.map(row => Object.values(row).join(',')).join('\n');
        const csvData = `${csvHeaders}\n${csvRows}`;
        const blob = new Blob([csvData], { type: 'text/csv;charset=utf-8;' });
        saveAs(blob, 'merged_output.csv');
        console.log('Data saved to merged_output.csv');
    };

    return (
        <Container>
            <Box my={4}>
                <Typography variant="h4" component="h1" gutterBottom>
                    Jungle Scout Product Explorer
                </Typography>
                <Box display="flex" alignItems="center" mb={2}>
                    <TextField
                        label="Enter keywords separated by commas"
                        variant="outlined"
                        fullWidth
                        value={keywords}
                        onChange={handleKeywordsChange}
                        style={{ marginRight: 10 }}
                    />
                    <Button variant="contained" color="primary" onClick={handleFetchData}>
                        Fetch Data
                    </Button>
                </Box>
                <Button variant="contained" color="secondary" onClick={saveToFile}>
                    Save to CSV
                </Button>
                <DataTable data={mergedData} />
                <ResponsiveContainer width="100%" height={400}>
                    <ScatterChart
                        margin={{ top: 20, right: 20, bottom: 20, left: 20 }}
                    >
                        <CartesianGrid />
                        <XAxis type="number" dataKey="price" name="Price" unit="$" />
                        <YAxis type="number" dataKey="sales" name="Sales" unit=" units" />
                        <Tooltip cursor={{ strokeDasharray: '3 3' }} />
                        <Scatter name="Products" data={mergedData} fill="#8884d8" />
                    </ScatterChart>
                </ResponsiveContainer>
            </Box>
        </Container>
    );
};

export default MainComponent;
