import React, { useState } from 'react';
import axios from 'axios';
import { TextField, Button, Container, Typography, Box } from '@mui/material';
import DataTable from './DataTable';

const MainComponent = () => {
    const [keywords, setKeywords] = useState('');
    const [data, setData] = useState([]);

    const handleKeywordsChange = (event) => {
        setKeywords(event.target.value);
        console.log('Keywords changed:', event.target.value);
    };

    const handleFetchData = async () => {
        const keywordList = keywords.split(',').map(keyword => keyword.trim());
        console.log('Fetching data for keywords:', keywordList);

        const apiKey = process.env.REACT_APP_JUNGLE_SCOUT_API_KEY;
        const keyName = process.env.REACT_APP_JUNGLE_SCOUT_KEY_NAME;
        const url = "https://developer.junglescout.com/api/product_database_query?marketplace=us&sort=name&page[size]=50";

        const headers = {
            'Authorization': `${keyName}:${apiKey}`,
            'X-API-Type': 'junglescout',
            'Accept': 'application/vnd.junglescout.v1+json',
            'Content-Type': 'application/vnd.api+json',
        };

        try {
            const response = await axios.post(url, {
                data: { attributes: { keyword: keywords, marketplace: 'us' } },
            }, { headers });

            console.log('API response received:', response);

            if (response.data && response.data.data) {
                const results = response.data.data.map(item => ({
                    asin: item.id,
                    title: item.attributes.title,
                    brand: item.attributes.brand,
                    price: item.attributes.price,
                    reviews: item.attributes.reviews,
                    rating: item.attributes.rating,
                    category: item.attributes.category,
                    sales: item.attributes.approximate_30_day_units_sold,
                    revenue: item.attributes.approximate_30_day_revenue,
                    imageUrl: item.attributes.image_url,
                    sellerType: item.attributes.seller_type,
                    dateFirstAvailable: item.attributes.date_first_available,
                }));

                console.log('Flattened results:', results);
                setData(results);
            } else {
                console.error('Invalid response format:', response);
            }
        } catch (error) {
            console.error("API request failed:", error);
        }
    };

    return (
        <Container>
            <Box my={4}>
                <Typography variant="h4" component="h1" gutterBottom>
                    Jungle Scout Product Explorer
                </Typography>
                <Box display="flex" alignItems="center" mb={2}>
                    <TextField
                        label="Enter keywords separated by commas"
                        variant="outlined"
                        fullWidth
                        value={keywords}
                        onChange={handleKeywordsChange}
                        style={{ marginRight: 10 }}
                    />
                    <Button variant="contained" color="primary" onClick={handleFetchData}>
                        Fetch Data
                    </Button>
                </Box>
                <DataTable data={data} />
            </Box>
        </Container>
    );
};

export default MainComponent;
