import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { makeStyles } from '@material-ui/core/styles';
import { AppBar, Tabs, Tab, Container, Paper, TableContainer, TextField, IconButton, Toolbar, Box } from '@material-ui/core';
import { Brightness4, Brightness7 } from '@material-ui/icons';
import ScatterPlot from './components/ScatterPlot';
import DataTable from './components/DataTable';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        backgroundColor: theme.palette.background.paper,
    },
    table: {
        minWidth: 650,
        backgroundColor: theme.palette.background.default,
    },
    appBar: {
        marginBottom: theme.spacing(2),
    },
    searchContainer: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: theme.spacing(2),
    },
    searchField: {
        flexGrow: 1,
        marginRight: theme.spacing(2),
    },
    tableContainer: {
        maxHeight: '60vh',
    },
    toolbar: {
        display: 'flex',
        justifyContent: 'space-between',
    },
    toggleButton: {
        marginLeft: 'auto',
    }
}));

const MainComponent = () => {
    const classes = useStyles();
    const [data, setData] = useState([]);
    const [filteredData, setFilteredData] = useState([]);
    const [tabIndex, setTabIndex] = useState(0);
    const [keyword, setKeyword] = useState('');
    const [darkMode, setDarkMode] = useState(false);
    const [resultCount, setResultCount] = useState(0);

    useEffect(() => {
        if (keyword) {
            fetchData(keyword);
        }
    }, [keyword]);

    const fetchData = async (keyword) => {
        try {
            const apiKey = 'your_api_key';
            const keyName = 'your_key_name';
            const headers = {
                'Authorization': `${keyName}:${apiKey}`,
                'X-API-Type': 'junglescout',
                'Accept': 'application/vnd.junglescout.v1+json',
                'Content-Type': 'application/vnd.api+json'
            };

            const response = await axios.post(
                'https://developer.junglescout.com/api/product_database_query?marketplace=us&sort=-revenue&page[size]=50',
                {
                    data: {
                        type: 'product_database_query',
                        attributes: {
                            include_keywords: [keyword],
                            exclude_unavailable_products: true
                        }
                    }
                },
                { headers }
            );

            if (response.data && response.data.data) {
                const results = response.data.data;
                setResultCount(results.length);
                setData(results);
                setFilteredData(results);
            }
        } catch (error) {
            console.error('API request failed:', error);
        }
    };

    const handleTabChange = (event, newValue) => {
        setTabIndex(newValue);
        switch (newValue) {
            case 1: // Price Segments
                handlePriceSegments();
                break;
            case 2: // Winners
                handleWinners();
                break;
            case 3: // Brand
                handleBrandBreakdown();
                break;
            default:
                setFilteredData(data);
        }
    };

    const handlePriceSegments = () => {
        const priceSegments = {};
        data.forEach(item => {
            const price = item.attributes.price;
            const lowerBound = Math.floor(price / 5) * 5;
            const upperBound = lowerBound + 4.99;
            const segment = `$${lowerBound.toFixed(2)}-$${upperBound.toFixed(2)}`;
            if (!priceSegments[segment]) {
                priceSegments[segment] = {
                    priceRange: segment,
                    sales: 0,
                    revenue: 0,
                    items: []
                };
            }
            priceSegments[segment].sales += item.attributes.approximate_30_day_units_sold;
            priceSegments[segment].revenue += item.attributes.approximate_30_day_revenue;
            priceSegments[segment].items.push(item);
        });

        const segmentsArray = Object.values(priceSegments).filter(segment => segment.sales > 0);
        segmentsArray.sort((a, b) => parseFloat(a.priceRange.split('-')[0].replace('$', '')) - parseFloat(b.priceRange.split('-')[0].replace('$', '')));
        setFilteredData(segmentsArray);
    };

    const handleWinners = () => {
        const priceSegments = {};
        data.forEach(item => {
            const price = item.attributes.price;
            const lowerBound = Math.floor(price / 5) * 5;
            const upperBound = lowerBound + 4.99;
            const segment = `$${lowerBound.toFixed(2)}-$${upperBound.toFixed(2)}`;
            if (!priceSegments[segment] || (priceSegments[segment].revenue < item.attributes.approximate_30_day_revenue)) {
                priceSegments[segment] = {
                    ...item,
                    priceRange: segment
                };
            }
        });

        const winnersArray = Object.values(priceSegments);
        winnersArray.sort((a, b) => parseFloat(a.priceRange.split('-')[0].replace('$', '')) - parseFloat(b.priceRange.split('-')[0].replace('$', '')));
        setFilteredData(winnersArray);
    };

    const handleBrandBreakdown = () => {
        const brandData = {};
        data.forEach(item => {
            const brand = item.attributes.brand;
            if (!brandData[brand]) {
                brandData[brand] = {
                    brand: brand,
                    sales: 0
                };
            }
            brandData[brand].sales += item.attributes.approximate_30_day_units_sold;
        });

        const totalSales = Object.values(brandData).reduce((sum, brand) => sum + brand.sales, 0);
        const brandArray = Object.values(brandData).map(brand => ({
            ...brand,
            salesPercentage: ((brand.sales / totalSales) * 100).toFixed(2)
        }));

        setFilteredData(brandArray);
    };

    const handleSearch = () => {
        fetchData(keyword);
    };

    const toggleDarkMode = () => {
        setDarkMode(!darkMode);
    };

    return (
        <div className={classes.root}>
            <AppBar position="static" className={classes.appBar}>
                <Toolbar className={classes.toolbar}>
                    <Tabs value={tabIndex} onChange={handleTabChange} aria-label="simple tabs example">
                        <Tab label="All Results" />
                        <Tab label="Price Segments" />
                        <Tab label="Winners" />
                        <Tab label="Brand" />
                    </Tabs>
                    <IconButton
                        edge="end"
                        color="inherit"
                        onClick={toggleDarkMode}
                        className={classes.toggleButton}
                    >
                        {darkMode ? <Brightness7 /> : <Brightness4 />}
                    </IconButton>
                </Toolbar>
            </AppBar>
            <Container>
                <div className={classes.searchContainer}>
                    <TextField
                        className={classes.searchField}
                        label="Keyword"
                        variant="outlined"
                        value={keyword}
                        onChange={(e) => setKeyword(e.target.value)}
                    />
                    <Button variant="contained" color="primary" onClick={handleSearch}>
                        Search
                    </Button>
                </div>
                <p>Number of Results: {resultCount}</p>
                <ScatterPlot data={filteredData} />
                <TableContainer component={Paper} className={classes.tableContainer}>
                    <DataTable data={filteredData} tabIndex={tabIndex} darkMode={darkMode} />
                </TableContainer>
            </Container>
        </div>
    );
};

export default MainComponent;
