import React from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';
import './DataTable.css';

const styles = {
    tableHeader: {
        position: 'sticky',
        top: 0,
        backgroundColor: '#fff',
        zIndex: 2
    },
    summaryRow: {
        backgroundColor: 'yellow',
        fontWeight: 'bold',
        position: 'sticky',
        top: 50,
        zIndex: 1
    },
    imageCell: {
        position: 'relative',
    },
    image: {
        width: 50,
        height: 50,
        transition: 'transform 0.2s',
    },
    imageHover: {
        position: 'absolute',
        width: 150,
        height: 150,
        zIndex: 10,
        transform: 'scale(3)',
        transition: 'transform 0.2s',
    }
};

class DataTable extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            hoverIndex: null,
        };
    }

    handleMouseEnter = (index) => {
        this.setState({ hoverIndex: index });
    }

    handleMouseLeave = () => {
        this.setState({ hoverIndex: null });
    }

    render() {
        const { data } = this.props;
        const { hoverIndex } = this.state;

        if (!data.length) {
            console.log('No data to display');
            return null;
        }

        console.log('Displaying data:', data);

        const headers = Object.keys(data[0]);

        return (
            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow style={styles.tableHeader}>
                            <TableCell>Image</TableCell>
                            {headers.filter(header => header !== 'imageUrl').map((header) => (
                                <TableCell key={header}>{header}</TableCell>
                            ))}
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {data.map((row, index) => (
                            <TableRow key={index} style={row.asin === "Summary" ? styles.summaryRow : {}}>
                                <TableCell style={styles.imageCell}>
                                    {row.imageUrl && (
                                        <img
                                            src={row.imageUrl}
                                            alt={row.title}
                                            style={hoverIndex === index ? { ...styles.image, ...styles.imageHover } : styles.image}
                                            onMouseEnter={() => this.handleMouseEnter(index)}
                                            onMouseLeave={this.handleMouseLeave}
                                        />
                                    )}
                                </TableCell>
                                {headers.filter(header => header !== 'imageUrl').map((header) => (
                                    <TableCell key={header} className="table-cell">{header === 'price' || header === 'revenue' ? `$${parseFloat(row[header]).toLocaleString()}` : row[header]}</TableCell>
                                ))}
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        );
    }
}

export default DataTable;
