import React from 'react';
import {
    ScatterChart, Scatter, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer
} from 'recharts';
import './ScatterPlot.css';

const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
        const { price, sales, imageUrl, title } = payload[0].payload;
        return (
            <div className="custom-tooltip">
                <p className="label">{`Price: $${price}`}</p>
                <p className="label">{`Sales: ${sales}`}</p>
                {imageUrl && <img src={imageUrl} alt={title} className="tooltip-image" />}
                <p className="label tooltip-title">{title}</p>
            </div>
        );
    }

    return null;
};

const ScatterPlot = ({ data }) => {
    const scatterData = data.map(item => ({
        price: item.price,
        sales: item.sales,
        imageUrl: item.imageUrl,
        title: item.title,
    }));

    return (
        <ResponsiveContainer width="100%" height={400}>
            <ScatterChart
                margin={{
                    top: 20, right: 20, bottom: 10, left: 10,
                }}
            >
                <CartesianGrid />
                <XAxis type="number" dataKey="price" name="Price" unit="$" />
                <YAxis type="number" dataKey="sales" name="Sales" />
                <Tooltip content={<CustomTooltip />} cursor={{ strokeDasharray: '3 3' }} />
                <Scatter name="Sales per Price" data={scatterData} fill="#8884d8" />
            </ScatterChart>
        </ResponsiveContainer>
    );
}

export default ScatterPlot;
