import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { makeStyles } from '@material-ui/core/styles';
import { AppBar, Tabs, Tab, Container, Paper, TableContainer } from '@material-ui/core';
import ScatterPlot from './components/ScatterPlot';
import DataTable from './components/DataTable';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        backgroundColor: theme.palette.background.paper,
    },
    table: {
        minWidth: 650,
        backgroundColor: theme.palette.background.default,
    },
    appBar: {
        marginBottom: theme.spacing(2),
    },
}));

const MainComponent = () => {
    const classes = useStyles();
    const [data, setData] = useState([]);
    const [filteredData, setFilteredData] = useState([]);
    const [tabIndex, setTabIndex] = useState(0);
    const [darkMode, setDarkMode] = useState(false);
    const [resultCount, setResultCount] = useState(0);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const apiKey = 'your_api_key';
                const keyName = 'your_key_name';
                const headers = {
                    'Authorization': `${keyName}:${apiKey}`,
                    'X-API-Type': 'junglescout',
                    'Accept': 'application/vnd.junglescout.v1+json',
                    'Content-Type': 'application/vnd.api+json'
                };

                const response = await axios.post(
                    'https://developer.junglescout.com/api/product_database_query?marketplace=us&sort=-revenue&page[size]=50',
                    {
                        data: {
                            type: 'product_database_query',
                            attributes: {
                                include_keywords: ['garden soil'],
                                exclude_unavailable_products: true
                            }
                        }
                    },
                    { headers }
                );

                if (response.data && response.data.data) {
                    const results = response.data.data;
                    setResultCount(results.length);
                    setData(results);
                    setFilteredData(results);
                }
            } catch (error) {
                console.error('API request failed:', error);
            }
        };

        fetchData();
    }, []);

    const handleTabChange = (event, newValue) => {
        setTabIndex(newValue);
    };

    const toggleDarkMode = () => {
        setDarkMode(!darkMode);
    };

    return (
        <div className={classes.root}>
            <AppBar position="static" className={classes.appBar}>
                <Tabs value={tabIndex} onChange={handleTabChange} aria-label="simple tabs example">
                    <Tab label="All Results" />
                    <Tab label="Price Segments" />
                    <Tab label="Winners" />
                    <button onClick={toggleDarkMode}>Toggle Dark Mode</button>
                </Tabs>
            </AppBar>
            <Container>
                <p>Number of Results: {resultCount}</p>
                <ScatterPlot data={filteredData} />
                <TableContainer component={Paper}>
                    <DataTable data={filteredData} tabIndex={tabIndex} darkMode={darkMode} />
                </TableContainer>
            </Container>
        </div>
    );
};

export default MainComponent;
